
#include <gtest/gtest.h>
#include <gmock/gmock.h>
#include <webcc/logger.h>

#include "FaceDetectionService.h"

using ::testing::MatchesRegex;
using ::testing::DoAll;
using ::testing::InvokeWithoutArgs;
using ::testing::Return;
using ::testing::_;

// Make subclass of FaceDetectionService so we can test its protected methods

class TestFaceDetectionService : public FaceDetectionService {
public:
    explicit TestFaceDetectionService(int iSleepSeconds, CascadeClassifier &cascade)
            : FaceDetectionService(iSleepSeconds, cascade) { }
public:
    void Get(const webcc::UrlQuery& query, webcc::RestResponse* response) { FaceDetectionService::Get(query, response); }
    void Post(const std::string& request_content, webcc::RestResponse* response) { FaceDetectionService::Post(request_content, response); }
};

// Test GET response

TEST(FaceDetectionService, Get)
{
    // Test Get method

    CascadeClassifier cascadeClassifier;
    auto pFaceDetectionService = new TestFaceDetectionService(0, cascadeClassifier);
    ASSERT_NE(pFaceDetectionService, nullptr);

    webcc::UrlQuery query;
    webcc::RestResponse response;
    pFaceDetectionService->Get(query, &response);

    ASSERT_EQ(response.status, webcc::Status::kOK);

    // Test with non-zero sleep time

    delete pFaceDetectionService;
    pFaceDetectionService = new TestFaceDetectionService(1, cascadeClassifier);
    ASSERT_NE(pFaceDetectionService, nullptr);

    pFaceDetectionService->Get(query, &response);
    ASSERT_EQ(response.status, webcc::Status::kOK);
}


static string sBlank40x40Jpg = "";

static string sOneFaceJpg = "";

// Test POST response

TEST(FaceDetectionService, Post)
{
    WEBCC_LOG_INIT("", webcc::LOG_CONSOLE);

    // Test real constructor

    CascadeClassifier cascadeClassifier;
    testing::internal::CaptureStderr();
    auto pFDS = new FaceDetectionService(0, cascadeClassifier);
    std::string output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(".*Cascade is empty!.*"));
    ASSERT_NE(pFDS, nullptr);

    // Remaining tests use test subclass. Empty cascade generates error log

    testing::internal::CaptureStderr();
    auto pFaceDetectionService = new TestFaceDetectionService(0, cascadeClassifier);
    output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(".*Cascade is empty!.*"));
    ASSERT_NE(pFaceDetectionService, nullptr);

    // Request arriving when no cascade available generates internal server error

    std::string sRequestContent = "";
    webcc::RestResponse response;
    testing::internal::CaptureStderr();
    pFaceDetectionService->Post(sRequestContent, &response);
    output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(".*Face detection cascade is invalid.*"));
    ASSERT_EQ(response.status, webcc::Status::kInternalServerError);

    // Load valid cascade

    cascadeClassifier.load("/home/dev/devops_workshop/facedetection_va/haarcascade_frontalface_alt.xml");
    ASSERT_FALSE(cascadeClassifier.empty());
    delete pFaceDetectionService;
    pFaceDetectionService = new TestFaceDetectionService(0, cascadeClassifier);

    // Empty request generates error log

    testing::internal::CaptureStderr();
    pFaceDetectionService->Post(sRequestContent, &response);
    output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(".*Got empty request.*"));
    ASSERT_EQ(response.status, webcc::Status::kBadRequest);

    // Valid JSON without any fields

    sRequestContent = "{ }";
    testing::internal::CaptureStderr();
    pFaceDetectionService->Post(sRequestContent, &response);
    output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(".*Could not extract image from POST data.*"));
    ASSERT_EQ(response.status, webcc::Status::kBadRequest);

    // Invalid JSON

    sRequestContent = "{ \"fac }";
    testing::internal::CaptureStderr();
    pFaceDetectionService->Post(sRequestContent, &response);
    output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(".*Missing '}' or object.*Could not extract image from POST data.*"));
    ASSERT_EQ(response.status, webcc::Status::kBadRequest);

    // Valid faceId but no imageData

    sRequestContent = "{ \"faceId\" : \"matt\" }";
    testing::internal::CaptureStderr();
    pFaceDetectionService->Post(sRequestContent, &response);
    output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(".*Could not extract image from POST data.*"));
    ASSERT_EQ(response.status, webcc::Status::kBadRequest);

    // imageData that is not a valid image

    sRequestContent = "{ \"faceId\" : \"matt\", \"imageData\" : \"abc\" }";
    testing::internal::CaptureStderr();
    pFaceDetectionService->Post(sRequestContent, &response);
    output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(".*Image data invalid.*"));
    ASSERT_EQ(response.status, webcc::Status::kBadRequest);

    // Valid image without a face

    sRequestContent = "{ \"faceId\" : \"matt\", \"imageData\" : \"" + sBlank40x40Jpg + "\" }";
    testing::internal::CaptureStderr();
    pFaceDetectionService->Post(sRequestContent, &response);
    output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(""));
    ASSERT_EQ(response.status, webcc::Status::kCreated);
    EXPECT_THAT(response.content, MatchesRegex(".*faceId.*matt.*faces.*"));

    // Valid image with a face

    sRequestContent = "{ \"faceId\" : \"matt\", \"imageData\" : \"" + sOneFaceJpg + "\" }";
    testing::internal::CaptureStderr();
    pFaceDetectionService->Post(sRequestContent, &response);
    output = testing::internal::GetCapturedStderr();
    EXPECT_THAT(output, MatchesRegex(""));
    ASSERT_EQ(response.status, webcc::Status::kCreated);
    EXPECT_THAT(response.content, MatchesRegex(".*faceId.*matt.*faces.*height.*154.*width.*154.*x.*298.*y.*120.*"));

    delete pFaceDetectionService;
}

TEST(Image, constructor)
{
    Image *pImage = new Image();
    ASSERT_NE(pImage, nullptr);
    ASSERT_EQ("", pImage->getFaceId());
    pImage->setImageData("");
    delete pImage;
}
